import React from "react";


function Detail(props) {
  return (
    <div className="productWrapper" >
      <img alt="" src={props.item.image} />
      <p>{props.item.name}</p>
      <p className="product-price">{props.detail.price} KD</p>
      <p>{props.item.description}</p>
    </div>
  );
}

export default Detail;
